import React, { useState } from "react";
import "./authentification.css";

const Authentication = () => {
  const [isLogin, setIsLogin] = useState(true);

  const toggleAuthMode = () => {
    setIsLogin(!isLogin);
  };

  return (
    <div
      className="auth_total_page_div"
      style={{
        backgroundImage: 'url("../../assets/h1.jpg")',  // Path to the image
        // Optional: creates a parallax effect
        height: "100vh",  // Make sure it fills the entire height of the viewport
      }}
    >
      <div className={`auth-box ${isLogin ? "login-box" : "sign_up-box"}`}>
        <h2>{isLogin ? "Login" : "Sign Up"}</h2>
        <form>
          {!isLogin && (
            <>
              {/* Fields for Signup */}
              <div className="user-box">
                <input
                  type="text"
                  placeholder="Enter Full Name"
                  className="user-input"
                />
              </div>
              <div className="user-box">
                <input
                  type="text"
                  placeholder="Enter Higher School Name"
                  className="user-input"
                />
              </div>
            </>
          )}

          {/* Shared Fields */}
          <div className="user-box">
            <input
              type="email"
              placeholder="Enter Email Address"
              className="user-input"
            />
          </div>
          <div className="user-box">
            <input
              type="password"
              placeholder="Enter Password"
              className="user-input"
            />
          </div>

          {!isLogin && (
            <div className="user-box">
              <input
                type="tel"
                placeholder="Enter Telephone Number"
                className="user-input"
              />
            </div>
          )}

          {/* Action Buttons */}
          <button type="submit" className="auth-button">
            {isLogin ? "Login" : "Sign Up"}
          </button>

          <div className="alert_message">
            <p>
              {isLogin
                ? "Don't have an account? "
                : "Already have an account? "}
              <button
                type="button"
                onClick={toggleAuthMode}
                className="toggle-auth-mode"
              >
                {isLogin ? "Sign Up" : "Login"}
              </button>
            </p>
          </div>

          <div className="alert_message">
            <p>
              Return to Home <a href="/">HOME</a>
            </p>
          </div>
        </form>
      </div>
    </div>
  );
};

export default Authentication;
