import React, { useState } from 'react';
import ProductDetails from './components/product';
import './home.css';
import logo from '../../assets/logo.png';
import { FaShoppingCart } from 'react-icons/fa'; // Import shopping cart icon

const Home = () => {
  const [cart, setCart] = useState([]);

  const products = [
    { id: 1, name: 'Product 1', price: 29.99, image: 'https://via.placeholder.com/150' },
    { id: 2, name: 'Product 2', price: 49.99, image: 'https://via.placeholder.com/150' },
    { id: 3, name: 'Product 3', price: 19.99, image: 'https://via.placeholder.com/150' },
  ];

  const addToCart = (product) => {
    setCart((prevCart) => [...prevCart, product]);
  };

  return (
    <div className="total-container">
      {/* Header Section */}
      <div className="header">
        <img src={logo} alt="E-Market Logo" className="logo" />
        <h1>Jibelna Zimni E-Market</h1>
        <div className="cart">
          <FaShoppingCart size={30} />
          <span className="cart-counter">Cart: {cart.length}</span>
        </div>
        <div className="auth-buttons">
          <button>Sign In</button>
          <button>Login</button>
        </div>
      </div>

      {/* Product Section */}
      <div className="main">
        <h2 className='message'>Our Products</h2>
        <div className="product-container">
          {products.map((product) => (
            <ProductDetails key={product.id} product={product} addToCart={addToCart} />
          ))}
        </div>
      </div>

      {/* Cart List Section */}
      {cart.length > 0 && (
        <div className="cart-list">
          <h3>Products in Cart</h3>
          <ul>
            {cart.map((product, index) => (
              <li key={index}>
                <img src={product.image} alt={product.name} width={50} />
                <span>{product.name}</span> - ${product.price}
              </li>
            ))}
          </ul>
        </div>
      )}

      {/* Footer Section */}
      <div className="footer">
        <p>&copy; 2024 E-Commerce. All rights reserved.</p>
      </div>
    </div>
  );
};

export default Home;
