import React from 'react';
import ProductDetails from './ProductDetails';
import './home.css';
const Home = () => {
  const products = [
    { id: 1, name: 'Product 1', price: 29.99, image: 'https://via.placeholder.com/150' },
    { id: 2, name: 'Product 2', price: 49.99, image: 'https://via.placeholder.com/150' },
    { id: 3, name: 'Product 3', price: 19.99, image: 'https://via.placeholder.com/150' },
  ];

  return (
    <div style={{ fontFamily: 'Arial, sans-serif' }}>
      {/* Header */}
      <header style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', padding: '1rem', backgroundColor: '#f8f8f8', borderBottom: '1px solid #ddd' }}>
        <h1 style={{ margin: 0 }}>E-Commerce</h1>
        <div>
          <button style={buttonStyle}>Sign In</button>
          <button style={{ ...buttonStyle, marginLeft: '0.5rem' }}>Login</button>
        </div>
      </header>

      {/* Product Section */}
      <main style={{ padding: '2rem' }}>
        <h2 style={{ textAlign: 'center' }}>Our Products</h2>
        <div style={productContainerStyle}>
          {products.map((product) => (
            <ProductDetails key={product.id} product={product} />
          ))}
        </div>
      </main>
    </div>
  );
};

const buttonStyle = {
  padding: '0.5rem 1rem',
  backgroundColor: '#007BFF',
  color: '#fff',
  border: 'none',
  borderRadius: '5px',
  cursor: 'pointer',
};

const productContainerStyle = {
  display: 'grid',
  gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))',
  gap: '2rem',
};

export default Home;
